query:
query propertyDetailsSearch($PropertyDetailsRequest: PropertyDetailsRequest!, $ContentImagesRequest: ContentImagesRequest!, $ContentReviewSummariesRequest: ContentReviewSummariesRequest, $ContentReviewScoreRequest: ContentReviewScoreRequest, $ContentInformationSummaryRequest: ContentInformationSummaryRequest, $ContentHighlightsRequest: ContentHighlightsRequest, $ContentLocalInformationRequest: ContentLocalInformationRequest, $ContentInformationRequest: ContentInformationRequest, $ContentFeaturesRequest: ContentFeaturesRequest, $PriceStreamMetaLabRequest: PriceStreamMetaLabRequest!) {
  propertyDetailsSearch(PropertyDetailsRequest: $PropertyDetailsRequest) {
    propertyDetails {
      propertyId
      propertyMetaInfo {
        propertyMetaRanking {
          numberOfProperty
          metrics {
            metricName
            rank
            absoluteValue
          }
        }
      }
      contentDetail {
        propertyId
        contentImages(ContentImagesRequest: $ContentImagesRequest) {
          hotelImages {
            caption
            groupEntityId
            groupId
            id
            providerId
            typeId
            uploadedDate
            highResolutionSizes
            urls {
              key
              value
            }
          }
          videos {
            id
            location
          }
          matterports {
            id
            orderId
            roomTypeId
            thumbnailUrl
            url
          }
        }
        contentReviewSummaries(ContentReviewSummariesRequest: $ContentReviewSummariesRequest) {
          snippets {
            snippetId
            countryCode
            countryId
            countryName
            date
            demographicId
            demographicName
            reviewer
            reviewRating
            snippet
            topics {
              score
              topicId
            }
          }
          recommendationScores {
            frequentTravellerRecommendationScore
            recommendationScore
          }
          positiveMentions {
            categories {
              id
              name
              score
            }
            facilityClassesSentiment {
              facilityIds
              id
              name
              noOfPositiveMentioned
            }
            facilityClasses {
              facilityIds
              id
              name
              noOfMentioned
            }
          }
        }
        contentReviewScore(ContentReviewScoreRequest: $ContentReviewScoreRequest) {
          combinedReviewScore {
            cumulative {
              maxScore
              reviewCount
              score
            }
          }
          providerReviewScore {
            isDefault
            providerId
            trendingScore {
              past14DaysUplift
              past30DaysUplift
            }
            cumulative {
              maxScore
              reviewCount
              score
            }
            demographics {
              allGuest {
                id
                scoreDistribution {
                  id
                  reviewCount
                }
                reviewCount
                grades {
                  id
                  score
                  cityAverage
                  subGrades {
                    score
                    name
                  }
                }
              }
            }
          }
          thirdPartyReviewScore {
            providerId
            grades {
              id
              score
            }
          }
        }
        contentSummary(ContentInformationSummaryRequest: $ContentInformationSummaryRequest) {
          propertyId
          displayName
          defaultName
          localeName
          accommodation {
            accommodationType
            accommodationName
          }
          propertyType
          address {
            address1
            address2
            countryCode
            area {
              id
              name
            }
            city {
              id
              name
            }
            country {
              id
              name
            }
            postalCode
            stateInfo {
              id
            }
          }
          awardsAndAccolades {
            goldCircleAward {
              year
            }
            advanceGuaranteeProgram {
              logo
              description
            }
          }
          remarks {
            renovationInfo {
              year
              renovationType
            }
          }
          hasHostExperience
          geoInfo {
            latitude
            longitude
          }
          rating
          asqType
          asqInfos {
            asqTypeId
          }
        }
        contentEngagement {
          peopleLooking
          todayBooking
        }
        contentHighlights(ContentHighlightsRequest: $ContentHighlightsRequest) {
          favoriteFeatures {
            category
            id
            images {
              id
              urls {
                key
                value
              }
            }
            name
            symbol
            tooltip
          }
          locationHighlightMessage {
            title
          }
          locationHighlights {
            distanceKm
            highlightType
            message
          }
          locations {
            tooltip
            symbol
            name
            images {
              id
              urls {
                key
                value
              }
            }
          }
          atfPropertyHighlights {
            id
            name
            symbol
            icon
            category
            images {
              id
              urls {
                key
                value
              }
            }
            tooltip
          }
        }
        contentLocalInformation(ContentLocalInformationRequest: $ContentLocalInformationRequest) {
          walkablePlaces {
            title
            totalCount
            description
            walkableCategories {
              categoryName
              totalCount
              topPlaces {
                name
                distanceInKm
                images {
                  urls {
                    value
                  }
                }
              }
            }
          }
          nearbyProperties {
            categoryName
            categorySymbol
            id
            places {
              abbr
              distanceInKm
              duration
              durationIcon
              geoInfo {
                latitude
                longitude
                obfuscatedLat
                obfuscatedLong
              }
              images {
                urls {
                  value
                  key
                }
                id
              }
              landmarkId
              name
              typeId
              typeName
            }
          }
          cuisines {
            id
            images {
              urls {
                value
                key
              }
              id
            }
            name
            restaurants {
              cuisinesOffered
              distance
              id
              name
            }
          }
          locationSubscore {
            airportScore
            poiScore
            transportationScore
          }
          nearbyPlaces {
            abbr
            distanceInKm
            duration
            durationIcon
            geoInfo {
              latitude
              longitude
              obfuscatedLat
              obfuscatedLong
            }
            images {
              urls {
                value
                key
              }
              id
            }
            landmarkId
            name
            typeId
            typeName
          }
          nearbyShops {
            abbr
            distanceInKm
            duration
            durationIcon
            geoInfo {
              latitude
              longitude
              obfuscatedLat
              obfuscatedLong
            }
            images {
              urls {
                value
                key
              }
              id
            }
            landmarkId
            name
            typeId
            typeName
          }
          popularLandmarkNumber
          topPlaces {
            abbr
            distanceInKm
            duration
            durationIcon
            geoInfo {
              latitude
              longitude
              obfuscatedLat
              obfuscatedLong
            }
            images {
              urls {
                value
                key
              }
              id
            }
            landmarkId
            name
            typeId
            typeName
          }
        }
        contentInformation(ContentInformationRequest: $ContentInformationRequest) {
          usefulInfoGroups {
            id
            usefulInfo {
              id
              description
            }
          }
          certificate {
            name
            imageUrl
            description
          }
          staffVaccinationInfo {
            details
            status
          }
          messaging {
            responsiveRate
          }
          description {
            short
          }
          notes {
            criticalNotes
          }
          sustainabilityInfo {
            isSustainableTravel
            practiceCategories {
              categoryId
              categoryName
              practices {
                practiceId
                practiceName
              }
            }
          }
        }
        contentFeatures(ContentFeaturesRequest: $ContentFeaturesRequest) {
          featureGroups {
            features {
              available
              featureName
              featureNameLocalizationList {
                locale
                value
              }
              id
              order
              symbol
              images {
                id
                urls {
                  key
                  value
                }
                groupId
                groupEntityId
                typeId
                uploadedDate
                providerId
                caption
                highResolutionSizes
              }
            }
            id
            name
            order
            symbol
          }
          hotelFacilities {
            id
            name
          }
          summary {
            chineseFriendly
            staycationFacilityIds {
              activities
              drinkingAndDining
              sportAndEntertainment
              wellness
            }
            hygienePlusFacilities {
              healthAndMedical
              safetyFeature
              preventiveEquipment
            }
          }
          facilityHighlights {
            facilityId
            facilityName
            images {
              id
              urls {
                key
                value
              }
              groupId
              groupEntityId
              typeId
              uploadedDate
              providerId
              caption
              highResolutionSizes
            }
          }
        }
      }
      metaLab(PriceStreamMetaLabRequest: $PriceStreamMetaLabRequest) {
        propertyAttributes {
          attributeId
          dataType
          value
          version
        }
      }
    }
  }
}
